const products=[{id:1,title:"Lenovo Yoga",price:3e3},{id:2,title:"Acer Aspire",price:1800},{id:3,title:"Dell Vostro",price:3400}];let order=[];function addToBasket(e){let r=products.find((r=>r.id===e)),t=!0;1===order.filter((r=>r.id===e)).length&&(t=!1),t?order.push(r):alert("Товар уже в корзине"),renderCart(),rerenderTotalPrice()}function removeFromBasket(e){order.splice(order.findIndex((r=>r.id===e)),1),renderCart(),rerenderTotalPrice()}function rerenderTotalPrice(){let e=order.reduce(((e,r)=>e+r.price),0);document.getElementById("total").innerText=e}function renderCart(){const e=document.getElementById("basket-items");e.innerHTML="",order.forEach((r=>{const t=document.createElement("li");t.innerText=r.title,t.onclick=()=>removeFromBasket(r.id),e.appendChild(t)}))}